mapper.update:
UPDATE course_categories
SET
    name = #{name},
    base_fee = #{baseFee}
WHERE id = #{id}

mapper.delete:
DELETE FROM course_categories
WHERE id = #{id}
AND NOT EXISTS (
    SELECT 1 FROM employees e
    WHERE e.category_id = #{id}
)
AND NOT EXISTS (
    SELECT 1 FROM courses c
    WHERE c.category_id = #{id}
)
