mapper.select:
SELECT * FROM employees e
WHERE e.category_id = #{categoryId}
AND e.id NOT IN (
    SELECT ecs.employee_id
    FROM employee_course_schedule ecs
    JOIN course_schedules cs ON ecs.schedule_id = cs.id
    WHERE cs.course_date = #{date}
      AND NOT (cs.end_period < #{startPeriod} OR cs.start_period > #{endPeriod})
)

mapper.delete:
DELETE e FROM employees e
WHERE e.id = #{id}
AND NOT EXISTS (
    SELECT 1 FROM employee_course_schedule ecs
    WHERE ecs.employee_id = #{id}
)
